import React from 'react';
import { Component } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
    margin-top: 20px;
    overfliow: hidden;
`;

const Wrapper = styled.div`
  height: 100%;
  
`;

const Slide = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    background-color: #B4B3E9;
`;

const ImgContainer = styled.div`
  flex: 1;
  height: 100%;

`;

const Image = styled.img`
  width: 80%;
`;

const InfoContainer = styled.h1`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 70px;
`;

const Desc = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`;

const Button = styled.button`
  padding: 10px;
  font-size: 20px;
  background-color: transparent;
  cursor: pointer;
`;

    


const SingleSlide = () => {
  return (
    <Container>
      <Wrapper>
      
      <Slide>
        <ImgContainer>
            <Image src='https://pngimg.com/uploads/cat/cat_PNG50533.png'/>
        </ImgContainer>

        <InfoContainer>
            <Title>CAT FANCIERS'</Title>
            <Desc>Aute minim ea fugiat veniam proident eiusmod sunt et ea reprehenderit ad occaecat.</Desc>
            <Button>KNOW MORE</Button>
        </InfoContainer>
        </Slide>

      </Wrapper>
    </Container>
  );
}

export default SingleSlide;

<Link to="/account">
                <Button color="white" className="is-rounded">
                  <span>My Account</span>
                </Button>
              </Link>
