import React from 'react';
import styled from 'styled-components';
import { SearchOutlined } from '@mui/icons-material';
import Badge from '@mui/material/Badge';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';    

import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Register from '../pages/Register';
import Login from '../pages/Login';
import Home from '../pages/Home';


const Container = styled.div`
    height: 60px;
    margin-bottom: 20px;
`;

const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
`;

// Designing the Left side components of the Navbar
const Left = styled.div`
    flex: 1;  
    display: flex;  
    align-items: center;
`;

const Language = styled.div`
    font-size: 14px;
    ${'' /* cursor: pointer; */}
`;

const SearchContainer = styled.div`
    border: 0.5px solid lightgray;
    display: flex;
    align-items: center;
    margin-left: 25px;
    padding: 5px;
    placeholder: "Search";
`;

const Input = styled.input`
    border: none;
`;

// Designing the Center side components of the Navbar
const Center = styled.div`
    flex: 1;   
    text-align: center; 
`;

const Logo = styled.h1`
    font-weight: bold;
`;

// Designing the Right side components of the Navbar
const Right = styled.div`
    flex: 1;  
    display: flex;
    align-items: center;
    justify-content: flex-end;
`;

const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin: 20px;
`;


const Navbar = () => {
  return (

    <Container>
        <Wrapper>

            {/* Left side part of the Navbar */}
            <Left>
                <Language>EN/LI</Language>
                <SearchContainer>
                    <Input /> 
                    <SearchOutlined style={{color: "gray", fontSize: 16}}/>
                </SearchContainer>
            </Left>
            
            {/* Center part of the Navbar */}
            <Center>
                <Logo>CATE.</Logo>
            </Center>

            {/* Right side part of the Navbar */}
            <Right>

                <Router>

                    <Routes>

                    <Route path="/" component={Home} />

                        <Route path="/register" component={Register} />

                        <Route path="/login" component={Login} />

                    </Routes>

                    <MenuItem>REGISTER</MenuItem>

                    <MenuItem>SIGN IN</MenuItem>

                </Router>
                
                <MenuItem>
                    <Badge badgeContent={2} color="primary">
                        <ShoppingCartOutlinedIcon />
                    </Badge>
                </MenuItem>
            </Right>

        </Wrapper>
    </Container>
  )
}

export default Navbar
