import React from 'react'
import styled from 'styled-components';
import ArrowLeftOutlinedIcon from '@mui/icons-material/ArrowLeftOutlined';
import ArrowRightOutlinedIcon from '@mui/icons-material/ArrowRightOutlined';

const Container = styled.div`
    display: flex;
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    ${'' /* background-color: #C2FAF9; */}
    position: relative;
`;

const Wrapper = styled.div`
    height: 100%;
`;

const Slide = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    ${'' /* justify-content: center; */}
`;

const ImgContainer = styled.div`
    flex: 1;  
    height: 100%;  
`;

const Image = styled.img`
    height: 80%;
`;

const InfoContainer = styled.div`
    flex: 1;
    padding: 50px;
`;

const Title = styled.h1`
    font-size: 70px;
`;

const Desc = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weight: 500;
    letter-spacing: 3px;
`;

const Button = styled.button`
    
`;

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #EDF6F9;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    ${'' /* putting the ternary condition here to check for the direction of the arrow and setting the position accordingly.. */}
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`;



function Slider() {
  return (
    <Container>

            {/* Left side of the arrow in the slider component */}
            <Arrow direction="left">
                <ArrowLeftOutlinedIcon />
            </Arrow>

            <Wrapper>
                <Slide>

                    <ImgContainer>
                        <Image src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgnL-6fb5PhgkCkH3odiqgPmhqoWm6HeRjwQ&usqp=CAU' />
                    </ImgContainer>

                    <InfoContainer>
                        <Title>SUMMER SALE</Title>
                        <Desc>Dolor velit esse sint et ad enim mollit tempor aute ullamco quis.</Desc>
                        <Button>SHOP NOW</Button>
                    </InfoContainer>

                </Slide>
            </Wrapper>

            {/* Right side arrow in the slidder component */}
            <Arrow direction="right">
                <ArrowRightOutlinedIcon />  
            </Arrow>

    </Container>
  )
}

export default Slider
