// -----------------------------------

const containerStyles = {
    height: "60px",
    marginBottom: "20px"
};

const wrapperStyles = {
    padding: "10px 20px", 
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center"
};

// Designing the LEFT SIDE components of the Navbar
// ------------------------------------------------

// const Left = styled.div`
