//     margin-left: 25px;
//     padding: 5px;
//     placeholder: "Search";
// `;

// const Input = styled.input`
//     border: none;
// `;

// ------------------------------------------------

const leftStyle = {
    flex: "1",  
    display: "flex",  
    alignItems: "center"
};

const language = {
    fontSize: "14px",
};

const searchContainer = {
    border: "0.5px solid lightgray",
    display: "flex",
    alignItems: "center",
    marginLeft: "25px",
    padding: "5px",
    placeholder: "Search"
};

const inputStyle = {
    border: "none"
};

// Designing the CENTER SIDE side components of the Navbar

// ------------------------------------------------
// const Center = styled.div`
//     flex: 1;   
//     text-align: center; 
// `;

// const Logo = styled.h1`
//     font-weight: bold;
// `;
// ------------------------------------------------

const centerStyle = {
    flex: "1",
    textAlign: "center"
};

const logoStyle = {
    fontWeight: "bold"
};


// Designing the RIGHT SIDE components of the Navbar

// ------------------------------------------------
// const Right = styled.div`
//     flex: 1;  
//     display: flex;
//     align-items: center;
//     justify-content: flex-end;
// `;

// ------------------------------------------------
const rightStyle = {
    flex: "1",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
};


const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin: 20px;
`;


const Navbar = () => {

    <div style={containerStyles}>
        <div style={wrapperStyles}>

            {/* Left Side */}
            <div style={leftStyle}>
            <div style={language}>EN/LI</div>
                 <div style={searchContainer}>
                     <input style={inputStyle}/>
                     <SearchOutlined style={{color: "gray", fontSize: 16}}/>
                 </div>
            </div>

            {/* Center Side */}
            <div style={centerStyle}>
                <h1 style={logoStyle}>CATE.</h1>
            </div>

            {/* Right Side */}
            <div style={rightStyle}>

            </div>

        </div>
    </div>
}


export default Navbar;

