import React, { useState } from 'react'
import styled from 'styled-components';
import ArrowLeftOutlinedIcon from '@mui/icons-material/ArrowLeftOutlined';
import ArrowRightOutlinedIcon from '@mui/icons-material/ArrowRightOutlined';

const Container = styled.div`
    display: flex;
    width: 100%;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    margin-top: 20px;
    overflow: hidden;
`;

const Wrapper = styled.div`
    height: 100%;
    display: flex;
    ${'' /* Since X-axis affects the width of the viewport and Y-axis affects the height of the viewport.. */}
    transform: translateX(0vw);
`;

const Slide = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    background-color: #${props=>props.bg}
`;

const ImgContainer = styled.div`
    flex: 1;  
    height: 100%;  
`;

const Image = styled.img`
    height: 80%;
`;

const InfoContainer = styled.div`
    flex: 1;
    padding: 50px;
`;

const Title = styled.h1`
    font-size: 70px;
`;

const Desc = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weight: 500;
    letter-spacing: 3px;
`;

const Button = styled.button`
    padding: 10px;
    font-size: 20px;
    background-color: transparent;
    cursor: pointer;
`;

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #EDF6F9;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    ${'' /* putting the ternary condition here to check for the direction of the arrow and setting the position accordingly.. */}
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
    z-index: 2;
`;



function Slider() {

    // Defining the handleClick for the direction, so that it could add the back and forward functionality to the arrows of the slidder.. 
    handleClick = (direction) => {
        // here I'm going to use the slide array, so the first element will be at position 0. So we start from there.. 
        const [slideIndex, setSlideIndex] = useState(0);

    };

  return (
    <Container>

            {/* Left side of the arrow in the slider component */}
            <Arrow direction="left" onClick={()=>handleClick("left")}>
                <ArrowLeftOutlinedIcon />
            </Arrow>

            <Wrapper>

                {/* First slidder of the Homepage.. */}
                <Slide bg="#B0EEF7">
                    <ImgContainer>
                        <Image src='https://pngimg.com/uploads/cat/cat_PNG50533.png' />
                    </ImgContainer>
                    <InfoContainer>
                        <Title>SUMMER SALE</Title>
                        <Desc>Dolor velit esse sint et ad enim mollit tempor aute ullamco quis.</Desc>
                        <Button>SHOP NOW</Button>
                    </InfoContainer>
                </Slide>

                {/* Second slidder of the Homepage.. */}
                <Slide bg="#978ADE">
                    <ImgContainer>
                        <Image src='https://pngimg.com/uploads/cat/cat_PNG50533.png' />
                    </ImgContainer>
                    <InfoContainer>
                        <Title>WINTER SALE</Title>
                        <Desc>Dolor velit esse sint et ad enim mollit tempor aute ullamco quis.</Desc>
                        <Button>SHOP NOW</Button>
                    </InfoContainer>
                </Slide>

                {/* Second slidder of the Homepage.. */}
                <Slide bg="#D9BAA7">
                    <ImgContainer>
                        <Image src='https://pngimg.com/uploads/cat/cat_PNG50533.png' />
                    </ImgContainer>
                    <InfoContainer>
                        <Title>POPULAR SALE</Title>
                        <Desc>Dolor velit esse sint et ad enim mollit tempor aute ullamco quis.</Desc>
                        <Button>SHOP NOW</Button>
                    </InfoContainer>
                </Slide>

            </Wrapper>

            {/* Right side arrow in the slidder component */}
            <Arrow direction="right" onClick={()=>handleClick("right")}>
                <ArrowRightOutlinedIcon />  
            </Arrow>

    </Container>
  )
}

export default Slider
