import React from 'react'
import styled from 'styled-components';

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    ${'' /* background-color: #B6EFFF; */}
    margin-top: 20px;
`;

const Wrapper = styled.div`
  height: 100%;
  
`;

const Slide = styled.div`
    width: 100vw;
    display: flex;
    align-items: center;
`;

const ImgContainer = styled.div`
  flex: 1;

`;

const Image = styled.img`
  width: 80%;
`;

const InfoContainer = styled.h1`
  flex: 1;
`;

const Title = styled.h1``;

const Desc = styled.p``;

const Button = styled.button``;

    


const SingleSlide = () => {
  return (
    <Container>
      <Wrapper>

        <ImgContainer>
            <Image src='https://pngimg.com/uploads/cat/cat_PNG50533.png'/>
        </ImgContainer>

        <InfoContainer>
            <Title>CAT FANCIERS'</Title>
            <Desc>Aute minim ea fugiat veniam proident eiusmod sunt et ea reprehenderit ad occaecat.</Desc>
            <Button>BUY NOW</Button>
        </InfoContainer>

      </Wrapper>
    </Container>
  );
}

export default SingleSlide;
